window.parseDifference = function(start, end){
    	// calc diff
        diff = ((( start - (Math.floor(end.getTime()) / 1000)))| 0);

        // extract time units
        minutes = ((diff / 60) % 60 ) | 0;
        seconds = (diff % 60) | 0;
        hours = (diff / 3600) | 0; 

        // prettify with a leading zero if less then ten per unit
        minutes = minutes < 10 ? "0" + minutes : minutes;
        seconds = seconds < 10 ? "0" + seconds : seconds;
        hours = hours < 10 ? "0" + hours : hours;

        return {
        	hours: hours,
        	minutes: minutes,
        	seconds: seconds,
        	diff
        }
}

class Countdown {
	constructor(endsAt, cb){
		this.endsAt = endsAt
		this.cb = cb
		this.destroyed = false
		this.Iterator(false)
	}

	Iterator(kill) {
		var $this = this
		let difference = parseDifference($this.endsAt, JUST.date())
		$this.cb(difference, difference.diff < 0)
		if(!kill && !$this.destroyed) setTimeout(() => {
			$this.Iterator(difference.diff < 0)
		}, 1000)
	}

	Destroy() {
		this.destroyed = true
	}
}

window.Countdown = Countdown

